import data from "../data"; 
import {React, useEffect, useState} from "react";

const DisplayFastFacts = ({search})=> {
    const [countryFastFacts, setCountryFastFacts] = useState({});

        // let countryName = country.name.official;
        // let population = country.population;
        // let capital = country.capital[0];
        // let region = country.region;
        // let currencies= country.currencies
        // let currency = currencies[Object.keys(currencies)[0]].name;

    useEffect(()=>{
        const handleCountryChange = async() =>{
            let data = await fetch(`http://localhost:5000//name/${search}`);
            let currencies = data.currencies;
            let newCountry = {
                flag: data.flags.png,
                countryName: data.name.official,
                population:data.population,
                capital:data.capital[0],
                region:data.region,
                currency:currencies[Object.keys(currencies)[0]].name

            }
            setCountryFastFacts(newCountry);
        }

        handleCountryChange()
        .catch(err=>{console.log(err)});
    },[search]);

    if(countryFastFacts !== null){
    return (
        <div className="fastFactsContainer">
            <div className="fastFactCountryInfo">
                <img alt="country" src={countryFastFacts.flag}></img>
                <h1 className="countryName">{countryFastFacts.countryName}</h1>
                <h3>Population: {countryFastFacts.population}</h3>
                <h3>Capital: {countryFastFacts.capital}</h3>
                <h3 >Region: {countryFastFacts.region}</h3>
                <h3>Currency: {countryFastFacts.currency}</h3>
            </div>
        </div>        
    );
    }
    else{
        return <div></div>;
    }

} 
const DisplayFullFacts = (props)=>{
    return (
        <div className="">

        </div>
    );
}

export  {DisplayFastFacts,DisplayFullFacts};
